import React, { useRef, useState, useCallback } from 'react';
import Webcam from 'react-webcam';

function CameraCapture({ onCapture }) {
  const webcamRef = useRef(null);
  const [countdown, setCountdown] = useState(null);

  // Updated video constraints for portrait mode
  const videoConstraints = {
    width: { ideal: 720 },
    height: { ideal: 1280 },
    aspectRatio: { ideal: 0.5625 }, // 9:16 portrait aspect ratio
    facingMode: 'user' // Front camera for selfie
  };

  const startCountdown = useCallback(() => {
    setCountdown(3);
    const timer = setInterval(() => {
      setCountdown((prev) => {
        if (prev === 1) {
          clearInterval(timer);
          capturePhoto();
          return null;
        }
        return prev - 1;
      });
    }, 1000);
  }, []);

  const capturePhoto = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    onCapture(imageSrc);
  }, [webcamRef, onCapture]);

  return (
    <div className="camera-capture">
      <h3>Take Your Photo</h3>
      <p>Position yourself in the center and smile!</p>

      <div className="camera-container">
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          videoConstraints={videoConstraints}
          className="webcam"
          mirrored={true} // Mirror the video like a selfie camera
          screenshotQuality={0.92} // High quality screenshots
        />

        {countdown && (
          <div className="countdown-overlay">
            <div className="countdown-number">{countdown}</div>
          </div>
        )}

        <div className="camera-guide">
          <div className="guide-frame"></div>
        </div>
      </div>

      <button 
        onClick={startCountdown} 
        className="btn btn-capture"
        disabled={countdown !== null}
      >
        {countdown !== null ? `${countdown}...` : 'ðŸ“¸ Take Photo'}
      </button>
    </div>
  );
}

export default CameraCapture;
